// 1 -> v > x + th
// 2 -> v < x - th
// 0 -> x - th <= v <= x + th
__device__ __forceinline__ int diffType(const int v, const int x, const int th)
{
	const int diff = x - v;

	return static_cast<int>(diff < -th) + (static_cast<int>(diff > th) << 1);
}

__device__ void calcMask(const uint C[4], const int v, const int th, int& mask1, int& mask2)
{
	mask1 = 0;
	mask2 = 0;

	int d1, d2;



	d1 = diffType(v, C[0] & 0xff, th);
	d2 = diffType(v, C[2] & 0xff, th);

	if ((d1 | d2) == 0)
		return;

	mask1 |= (d1 & 1) << 0;
	mask2 |= ((d1 & 2) >> 1) << 0;

	mask1 |= (d2 & 1) << 8;
	mask2 |= ((d2 & 2) >> 1) << 8;



	d1 = diffType(v, C[1] & 0xff, th);
	d2 = diffType(v, C[3] & 0xff, th);

	if ((d1 | d2) == 0)
		return;

	mask1 |= (d1 & 1) << 4;
	mask2 |= ((d1 & 2) >> 1) << 4;

	mask1 |= (d2 & 1) << 12;
	mask2 |= ((d2 & 2) >> 1) << 12;



	d1 = diffType(v, (C[0] >> (2 * 8)) & 0xff, th);
	d2 = diffType(v, (C[2] >> (2 * 8)) & 0xff, th);

	if ((d1 | d2) == 0)
		return;

	mask1 |= (d1 & 1) << 2;
	mask2 |= ((d1 & 2) >> 1) << 2;

	mask1 |= (d2 & 1) << 10;
	mask2 |= ((d2 & 2) >> 1) << 10;



	d1 = diffType(v, (C[1] >> (2 * 8)) & 0xff, th);
	d2 = diffType(v, (C[3] >> (2 * 8)) & 0xff, th);

	if ((d1 | d2) == 0)
		return;

	mask1 |= (d1 & 1) << 6;
	mask2 |= ((d1 & 2) >> 1) << 6;

	mask1 |= (d2 & 1) << 14;
	mask2 |= ((d2 & 2) >> 1) << 14;



	d1 = diffType(v, (C[0] >> (1 * 8)) & 0xff, th);
	d2 = diffType(v, (C[2] >> (1 * 8)) & 0xff, th);

	/*if ((d1 | d2) == 0)
		return;*/

	mask1 |= (d1 & 1) << 1;
	mask2 |= ((d1 & 2) >> 1) << 1;

	mask1 |= (d2 & 1) << 9;
	mask2 |= ((d2 & 2) >> 1) << 9;



	d1 = diffType(v, (C[0] >> (3 * 8)) & 0xff, th);
	d2 = diffType(v, (C[2] >> (3 * 8)) & 0xff, th);

	/*if ((d1 | d2) == 0)
		return;*/

	mask1 |= (d1 & 1) << 3;
	mask2 |= ((d1 & 2) >> 1) << 3;

	mask1 |= (d2 & 1) << 11;
	mask2 |= ((d2 & 2) >> 1) << 11;



	d1 = diffType(v, (C[1] >> (1 * 8)) & 0xff, th);
	d2 = diffType(v, (C[3] >> (1 * 8)) & 0xff, th);

	/*if ((d1 | d2) == 0)
		return;*/

	mask1 |= (d1 & 1) << 5;
	mask2 |= ((d1 & 2) >> 1) << 5;

	mask1 |= (d2 & 1) << 13;
	mask2 |= ((d2 & 2) >> 1) << 13;



	d1 = diffType(v, (C[1] >> (3 * 8)) & 0xff, th);
	d2 = diffType(v, (C[3] >> (3 * 8)) & 0xff, th);

	mask1 |= (d1 & 1) << 7;
	mask2 |= ((d1 & 2) >> 1) << 7;

	mask1 |= (d2 & 1) << 15;
	mask2 |= ((d2 & 2) >> 1) << 15;
}

// 1 -> v > x + th
// 2 -> v < x - th
// 0 -> not a keypoint
__device__ __forceinline__ bool isKeyPoint(int mask1, int mask2)
{
	return (__popc(mask1) > 8 && (c_table[(mask1 >> 3) - 63] & (1 << (mask1 & 7)))) ||
		   (__popc(mask2) > 8 && (c_table[(mask2 >> 3) - 63] & (1 << (mask2 & 7))));
}

__device__ int cornerScore(const uint C[4], const int v, const int threshold)
{
	// binary search in [threshold + 1, 255]

	int min = threshold + 1;
	int max = 255;

	while (min <= max)
	{
		const int mid = (min + max) >> 1;

		int mask1 = 0;
		int mask2 = 0;

		calcMask(C, v, mid, mask1, mask2);

		int isKp = static_cast<int>(isKeyPoint(mask1, mask2));

		min = isKp * (mid + 1) + (isKp ^ 1) * min;
		max = (isKp ^ 1) * (mid - 1) + isKp * max;
	}

	return min - 1;
}

template <bool calcScore>
__global__ void calcKeypoints(const uchar* imgData, int cols, int rows, int step, short2* kpLoc, const unsigned int maxKeypoints, int* score, const int threshold, unsigned int* d_counter)
{
	#if defined(__CUDA_ARCH__) && (__CUDA_ARCH__ >= 110)

	const int j = threadIdx.x + blockIdx.x * blockDim.x + 3;
	const int i = threadIdx.y + blockIdx.y * blockDim.y + 3;

	if (i < rows - 3 && j < cols - 3)
	{
		int v;
		uint C[4] = {0,0,0,0};
		//printf("IMG CST %d %d %d\n", i, j, access(imgData, step, i, j));

		C[2] |= static_cast<uint>(access(imgData, step, i - 3, j - 1)) << 8;
		C[2] |= static_cast<uint>(access(imgData, step, i - 3, j));
		C[1] |= static_cast<uint>(access(imgData, step, i - 3, j + 1)) << (3 * 8);

		C[2] |= static_cast<uint>(access(imgData, step, i - 2, j - 2)) << (2 * 8);
		C[1] |= static_cast<uint>(access(imgData, step, i - 2, j + 2)) << (2 * 8);

		C[2] |= static_cast<uint>(access(imgData, step, i - 1, j - 3)) << (3 * 8);
		C[1] |= static_cast<uint>(access(imgData, step, i - 1, j + 3)) << 8;

		C[3] |= static_cast<uint>(access(imgData, step, i, j - 3));
		v     = static_cast<int> (access(imgData, step, i, j));
		C[1] |= static_cast<uint>(access(imgData, step, i, j + 3));

		int d1 = diffType(v, C[1] & 0xff, threshold);
		int d2 = diffType(v, C[3] & 0xff, threshold);

		if ((d1 | d2) == 0)
			return;

		C[3] |= static_cast<uint>(access(imgData, step, i + 1, j - 3)) << 8;
		C[0] |= static_cast<uint>(access(imgData, step, i + 1, j + 3)) << (3 * 8);

		C[3] |= static_cast<uint>(access(imgData, step, i + 2, j - 2)) << (2 * 8);
		C[0] |= static_cast<uint>(access(imgData, step, i + 2, j + 2)) << (2 * 8);

		C[3] |= static_cast<uint>(access(imgData, step, i + 3, j - 1)) << (3 * 8);
		C[0] |= static_cast<uint>(access(imgData, step, i + 3, j));
		C[0] |= static_cast<uint>(access(imgData, step, i + 3, j + 1)) << 8;

		int mask1 = 0;
		int mask2 = 0;

		calcMask(C, v, threshold, mask1, mask2);

		if (isKeyPoint(mask1, mask2))
		{
			if (calcScore) access(score, step, i, j) = cornerScore(C, v, threshold);

			const unsigned int ind = atomicInc(d_counter, (unsigned int)(-1));

			if (ind < maxKeypoints)
				kpLoc[ind] = make_short2(j, i);
		}
	}

	#endif
}

void calcKeypoints_gpu(const uchar* imgData, int cols, int rows, int step, short2* kpLoc, int maxKeypoints, int* score, int threshold, unsigned int* d_counter, cudaStream_t stream, cudaEvent_t event)
{
	dim3 block(32, 8);

	dim3 grid;
	grid.x = divUp(cols - 6, block.x);
	grid.y = divUp(rows - 6, block.y);

	CUDA_SAFE_CALL( cudaMemsetAsync(d_counter, 0, sizeof(unsigned int), stream) );
	CUDA_SAFE_CALL( cudaMemsetAsync(score, 0, sizeof(unsigned int) * cols*rows, stream) );
	//CUDA_SAFE_CALL( cudaStreamSynchronize( stream) );

	if (score)
	{
		calcKeypoints<true><<<grid, block, 0, stream>>>(imgData, cols, rows, step, kpLoc, maxKeypoints, score, threshold, d_counter);
	} else {
		calcKeypoints<false><<<grid, block, 0, stream>>>(imgData, cols, rows, step, kpLoc, maxKeypoints, score, threshold, d_counter);
	}
	
	CUDA_SAFE_CALL( cudaGetLastError() );
	//CUDA_SAFE_CALL( cudaEventRecord(event, stream) );
}

