// nonmaxSuppression

__global__ void nonmaxSuppression(int step, const short2* kpLoc, unsigned int* count, int* scoreMat, short2* locFinal, float* responseFinal, unsigned int* d_counter)
{
	#if defined(__CUDA_ARCH__) && (__CUDA_ARCH__ >= 110)

	const int kpIdx = threadIdx.x + blockIdx.x * blockDim.x;

	if (kpIdx < *count)
	{
		short2 loc = kpLoc[kpIdx];

		int score = access(scoreMat, step, loc.y, loc.x);
		/*printf("CST %d %d %d %d %d %d %d %d %d %d %d\n", loc.y, loc.x, score,
		access(scoreMat, step, loc.y - 1, loc.x - 1),
		access(scoreMat, step, loc.y - 1, loc.x    ),
		access(scoreMat, step, loc.y - 1, loc.x + 1),
		access(scoreMat, step, loc.y    , loc.x - 1),
		access(scoreMat, step, loc.y    , loc.x + 1),
		access(scoreMat, step, loc.y + 1, loc.x - 1),
		access(scoreMat, step, loc.y + 1, loc.x    ),
		access(scoreMat, step, loc.y + 1, loc.x + 1)
		);*/
		bool ismax =
			score > access(scoreMat, step, loc.y - 1, loc.x - 1) &&
			score > access(scoreMat, step, loc.y - 1, loc.x    ) &&
			score > access(scoreMat, step, loc.y - 1, loc.x + 1) &&

			score > access(scoreMat, step, loc.y    , loc.x - 1) &&
			score > access(scoreMat, step, loc.y    , loc.x + 1) &&

			score > access(scoreMat, step, loc.y + 1, loc.x - 1) &&
			score > access(scoreMat, step, loc.y + 1, loc.x    ) &&
			score > access(scoreMat, step, loc.y + 1, loc.x + 1);

		if (ismax)
		{
			const unsigned int ind = atomicInc(d_counter, (unsigned int)(-1));

			locFinal[ind] = loc;
			responseFinal[ind] = static_cast<float>(score);
		}
	}

	#endif
}

